import React, { useReducer } from "react";
import "./index.css";

const initialState = {
  password: "",
  length: 10,
  includeUppercase: true,
  includeNumbers: true,
  includeSpecialChars: true,
};

const reducer = (state, action) => {
  switch (action.type) {
    case "SET_LENGTH":
      return { ...state, length: action.payload };
    case "TOGGLE_UPPERCASE":
      return { ...state, includeUppercase: !state.includeUppercase };
    case "TOGGLE_NUMBERS":
      return { ...state, includeNumbers: !state.includeNumbers };
    case "TOGGLE_SPECIAL_CHARS":
      return { ...state, includeSpecialChars: !state.includeSpecialChars };
    case "GENERATE_PASSWORD":
      return { ...state, password: generatePassword(state) };
    default:
      return state;
  }
};

const generatePassword = ({
  length,
  includeUppercase,
  includeNumbers,
  includeSpecialChars,
}) => {
  // Generate password logic here
  // ...
};

function PasswordGenerator() {
  const [state, dispatch] = useReducer(reducer, initialState);

  const handleGeneratePassword = () => {
    dispatch({ type: "GENERATE_PASSWORD" });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4">Password Generator</h2>
        <div className="mb-4">
          <label className="block mb-2 font-medium">Password Length:</label>
          <input
            type="number"
            value={state.length}
            onChange={(e) =>
              dispatch({ type: "SET_LENGTH", payload: e.target.value })
            }
            className="w-full p-2 border rounded-md"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2 font-medium">
            <input
              type="checkbox"
              checked={state.includeUppercase}
              onChange={() => dispatch({ type: "TOGGLE_UPPERCASE" })}
            />
            Include Uppercase
          </label>
          {/* Similar checkboxes for includeNumbers and includeSpecialChars */}
        </div>
        <button
          onClick={handleGeneratePassword}
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Generate Password
        </button>
        {state.password && (
          <div className="mt-4 p-2 bg-gray-200 rounded-md">
            {state.password}
          </div>
        )}
      </div>
    </div>
  );
}

export default PasswordGenerator;
