import React, { useReducer } from "react";
const dotenv = require('dotenv');


const reducer = (state, action) => {
  switch (action.type) {
    case "SET_LENGTH":
      return { ...state, length: action.payload };
    case "TOGGLE_INCLUDE_UPPERCASE":
      return { ...state, includeUppercase: !state.includeUppercase };
    case "TOGGLE_INCLUDE_NUMBERS":
      return { ...state, includeNumbers: !state.includeNumbers };
    case "TOGGLE_INCLUDE_SPECIAL_CHARS":
      return { ...state, includeSpecialChars: !state.includeSpecialChars };
    case "SET_PASSWORD":
      return { ...state, password: action.payload };
    default:
      return state;
  }
};

function PasswordGenerator() {
  const initialState = {
    password: "",
    length: 12,
    includeUppercase: true,
    includeNumbers: true,
    includeSpecialChars: true,
  };

  const [state, dispatch] = useReducer(reducer, initialState);

  const generatePassword = () => {
    const ENV = dotenv.config().parsed;

    const { length, includeUppercase, includeNumbers, includeSpecialChars } = state;
    // logic.
    const lowercaseChars = process.env.LOWERCASE_CHARS;
    const uppercaseChars = process.env.UPPERCASE_CHARS;
    const numberChars = process.env.NUMBER_CHARS;
    const specialChars = process.env.SPACIAL_CHARS;

    let allChars = lowercaseChars;
    if (includeUppercase) allChars += uppercaseChars;
    if (includeNumbers) allChars += numberChars;
    if (includeSpecialChars) allChars += specialChars;

     let generatedPassword = "";
     for (let i = 0; i < length; i++) {
       const randomIndex = Math.floor(Math.random() * allChars.length);
       generatedPassword += allChars.charAt(randomIndex);
     }

    dispatch({ type: "SET_PASSWORD", payload: generatedPassword });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4">Password Generator</h2>
        <div className="mb-4">
          <label className="block mb-2 font-medium">Password Length:</label>
          <input
            type="number"
            value={state.length}
            onChange={(e) =>
              dispatch({ type: "SET_LENGTH", payload: e.target.value })
            }
            className="w-full p-2 border rounded-md"
          />
        </div>
        {/* Similar components for toggling includeUppercase, includeNumbers, and includeSpecialChars */}
        <button
          onClick={generatePassword}
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Generate Password
        </button>
        {state.password && (
          <div className="mt-4 p-2 bg-gray-200 rounded-md">
            {state.password}
          </div>
        )}
      </div>
    </div>
  );
}

export default PasswordGenerator;
